((root, factory) => {
  if (typeof exports === 'object') {
    module.exports = factory();
  } else {
    root.prune = factory();
  }
})(this, () => {
  /**
   * prune data tree generated by `parse()`.
   * @param {ClassNode} node
   */
  function prune(node) {
    node.children = node.children
      .filter(childNode => Object.keys(childNode).length > 0)
      .filter(childNode => childNode.name.length > 0);

    node.children.forEach(prune);
  }

  return prune;
});
